import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

function ListMovies() {
  const [movies, setMovies] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const moviesPerPage = 12;

  useEffect(() => {
    async function fetchMovies() {
      const res = await fetch("/api/movies");
      const payload = await res.json();
      setMovies(payload.data);
    }
    fetchMovies();
  }, []);

  // Pagination logic
  const indexOfLastMovie = currentPage * moviesPerPage;
  const indexOfFirstMovie = indexOfLastMovie - moviesPerPage;
  const currentMovies = movies.slice(indexOfFirstMovie, indexOfLastMovie);

  const totalPages = Math.ceil(movies.length / moviesPerPage);

  const handleNext = () => {
    if (currentPage < totalPages) setCurrentPage(currentPage + 1);
  };

  const handlePrev = () => {
    if (currentPage > 1) setCurrentPage(currentPage - 1);
  };

  return (
    <div className="movies-wrapper">
      <h2 className="page-title">üé¨ <span style={{borderBottom:"6px solid royalblue",paddingBottom:"10px"}}>Movies</span> Collection</h2>
      <div className="movies-container">
        {currentMovies.map((movie) => (
          <div key={movie.id} className="movie-card" style={{backgroundColor:"#8dfff4"}}>
            <h3 style={{color:"#000000"}}>{movie.title}</h3>
            <p className="tagline">{movie.tagline || "No tagline"}</p>
            <p style={{color:"#000000"}}>‚≠ê {movie.vote_average} / 10</p>
            <Link to={`/movies/${movie.id}`} className="btn btn-dark">
              View Details ‚Üí
            </Link>
          </div>
        ))}
      </div>

      {/* Pagination Controls */}
      <div className="pagination">
        <button onClick={handlePrev} disabled={currentPage === 1}>
          ‚¨Ö Prev
        </button>
        <span>
          Page {currentPage} of {totalPages}
        </span>
        <button onClick={handleNext} disabled={currentPage === totalPages}>
          Next ‚û°
        </button>
      </div>
    </div>
  );
}

export default ListMovies;
