import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";

function MovieDetails() {
  const { id } = useParams();
  const [movie, setMovie] = useState(null);

  useEffect(() => {
    async function fetchMovie() {
      const res = await fetch(`/api/movies/${id}`);
      const payload = await res.json();
      setMovie(payload);
    }
    fetchMovie();
  }, [id]);

  if (!movie) return <p>Loading...</p>;

  // Fix release_date
  let localizedDate = movie.release_date;
  if (localizedDate) {
    const [day, month, year] = movie.release_date.split("/");
    localizedDate = new Date(`20${year}-${month}-${day}`).toLocaleDateString(navigator.language);
  }

  return (
    <div style={{ padding: "20px", maxWidth: "600px", margin: "auto" }}>
      <h2>{movie.title}</h2>
      {movie.tagline && <p style={{ fontStyle: "italic" }}>“{movie.tagline}”</p>}
      <p><strong>Overview:</strong> {movie.overview}</p>
      <p><strong>Release Date:</strong> {localizedDate}</p>
      <p><strong>Runtime:</strong> {movie.runtime} minutes</p>
      <p><strong>Status:</strong> {movie.status}</p>
      <p><strong>Rating:</strong> ⭐ {movie.vote_average} / 10</p>
      <p><strong>Votes:</strong> {movie.vote_count}</p>

      <Link to="/" style={{ display: "inline-block", marginTop: "20px", color: "#61dafb" }}>
        ⬅ Back to Movies List
      </Link>
    </div>
  );
}

export default MovieDetails;
