import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";

function MovieDetails() {
  const { id } = useParams(); // v5 hook still works
  const [movie, setMovie] = useState(null);

  useEffect(() => {
    async function fetchMovie() {
      const res = await fetch(`/api/movies/${id}`);
      const payload = await res.json();
      setMovie(payload);
    }
    fetchMovie();
  }, [id]);

  if (!movie) return <p>Loading...</p>;

  const localizedDate = new Date(movie.release_date).toLocaleDateString(
    navigator.language
  );

  return (
    <div style={{ padding: "20px" }}>
      <h2>{movie.title}</h2>
      <p><strong>Tagline:</strong> {movie.tagline}</p>
      <p><strong>Overview:</strong> {movie.overview}</p>
      <p><strong>Release Date:</strong> {localizedDate}</p>
      <p><strong>Runtime:</strong> {movie.runtime} minutes</p>
      <p><strong>Status:</strong> {movie.status}</p>
      <p><strong>Rating:</strong> {movie.vote_average} / 10</p>
      <p><strong>Votes:</strong> {movie.vote_count}</p>

      <Link to="/" style={{ display: "inline-block", marginTop: "20px" }}>
        â¬… Back to Movies List
      </Link>
    </div>
  );
}

export default MovieDetails;
